import{_ as e,g as a,f as r,v as i}from"./app.09129265.js";const m=JSON.parse('{"title":"\u827A\u672F\u5BB6\u4E3A\u4F55\u800C\u521B\u4F5C\uFF1F","description":"","frontmatter":{},"headers":[{"level":2,"title":"Daddy, where are you? | Tierney Gearon","slug":"daddy-where-are-you-tierney-gearon","link":"#daddy-where-are-you-tierney-gearon","children":[]},{"level":2,"title":"\u7B2C\u516B\u5929 \uFF5C \u9AD8\u5C71","slug":"\u7B2C\u516B\u5929-\u9AD8\u5C71","link":"#\u7B2C\u516B\u5929-\u9AD8\u5C71","children":[]},{"level":2,"title":"Mother \uFF5C Paul Graham","slug":"mother-paul-graham","link":"#mother-paul-graham","children":[]},{"level":2,"title":"\u6211\u4EEC\u53EF\u4EE5\u62CD\u4EC0\u4E48\uFF1F","slug":"\u6211\u4EEC\u53EF\u4EE5\u62CD\u4EC0\u4E48","link":"#\u6211\u4EEC\u53EF\u4EE5\u62CD\u4EC0\u4E48","children":[]},{"level":2,"title":"\u53C2\u8003","slug":"\u53C2\u8003","link":"#\u53C2\u8003","children":[]}],"relativePath":"notes/art/\u4ED6\u4EEC\u4E3A\u4F55\u800C\u521B\u4F5C.md","lastUpdated":1675321228000}'),t={name:"notes/art/\u4ED6\u4EEC\u4E3A\u4F55\u800C\u521B\u4F5C.md"},l=i('<h1 id="\u827A\u672F\u5BB6\u4E3A\u4F55\u800C\u521B\u4F5C" tabindex="-1">\u827A\u672F\u5BB6\u4E3A\u4F55\u800C\u521B\u4F5C\uFF1F <a class="header-anchor" href="#\u827A\u672F\u5BB6\u4E3A\u4F55\u800C\u521B\u4F5C" aria-hidden="true">#</a></h1><h2 id="daddy-where-are-you-tierney-gearon" tabindex="-1">Daddy, where are you? | Tierney Gearon <a class="header-anchor" href="#daddy-where-are-you-tierney-gearon" aria-hidden="true">#</a></h2><p>Tierney Gearon\u662F\u751F\u80B2\u4E86\u56DB\u4E2A\u5B69\u5B50\u7684\u5355\u8EAB\u6BCD\u4EB2\uFF0C\u4ED6\u7684\u68A6\u60F3\u662F\u62E5\u6709\u4E00\u4E2A\u7D27\u5BC6\u7684\u5BB6\u5EAD\u3002\u4F46\u662F\u5931\u8D25\u7684\u5A5A\u59FB\u751F\u6D3B\u8BA9\u5979\u610F\u8BC6\u5230\uFF0C\u4ED6\u662F\u5982\u6B64\u8FEB\u5207\u5730\u60F3\u8981\u5F97\u5230\u7231\u548C\u611F\u60C5\uFF0C\u4EE5\u81F3\u4E8E\u6CA1\u6709\u8DB3\u591F\u7684\u65F6\u95F4\u53BB\u4E86\u89E3\u5979\u7684\u4F34\u4FA3\u3002</p><p>\u867D\u7136\u5979\u58F0\u79F0\u81EA\u5DF1\u662F\u4E00\u4E2A\u6218\u6597\u529B\u5F3A\u5927\uFF0C\u5E76\u4E14\u4EAB\u53D7\u5468\u65CB\u4E8E\u5DE5\u4F5C\u548C\u5B69\u5B50\u4EEC\u4E4B\u95F4\u7684\u8FD9\u79CD\u751F\u6D3B\uFF0C\u4F46\u662F\u5728\u5979\u7684\u4E2A\u4EBA\u4F5C\u54C1\u4E2D\uFF0C\u5374\u5E38\u5E38\u6D6E\u73B0\u51FA\u4E00\u9053\u62B9\u4E0D\u53BB\u7684\u4F24\u75D5\u3002</p><p>Gearon\u7684\u6BCD\u4EB2\u662F\u8E81\u90C1\u75C7\u548C\u7CBE\u795E\u5206\u88C2\u75C7\u60A3\u8005\uFF0C\u60C5\u7EEA\u591A\u53D8\uFF0C\u7ECF\u5E38\u65E0\u6CD5\u81EA\u63A7\u3002\u5728Gearon\u7684\u7AE5\u5E74\u8BB0\u5FC6\u91CC\uFF0C\u867D\u7136\u7236\u6BCD\u90FD\u66FE\u5C3D\u529B\u7ED9\u4E88\u5B69\u5B50\u4EEC\u5173\u7231\u548C\u7167\u987E\uFF0C\u4F46\u6574\u4E2A\u5BB6\u5EAD\u8FD8\u662F\u65E0\u53EF\u633D\u56DE\u5730\u5272\u88C2\u3002\u5728\u5B69\u5B50\u4E2D\u6392\u884C\u8001\u5927\u7684Gearon\u4E0D\u5F97\u4E0D\u8FC7\u65E9\u5730\u6295\u5165\u5230\u6BCD\u6027\u7684\u89D2\u8272\uFF0C\u627F\u62C5\u8D77\u7167\u987E\u81EA\u5DF1\u548C\u5F1F\u5F1F\u4EEC\u7684\u8D23\u4EFB\u3002\u8FD9\u4E9B\u7ECF\u5386\u9020\u5C31\u4E86\u5979\u72EC\u7ACB\u8981\u5F3A\u7684\u4E2A\u6027\uFF0C\u4F46\u540C\u65F6\u4E5F\u7ED9\u5979\u7559\u4E0B\u4E86\u6325\u4E4B\u4E0D\u53BB\u7684\u9634\u5F71\u3002</p><p>\u5728\u6709\u4E86\u81EA\u5DF1\u7684\u5B69\u5B50\u540E\uFF0C\u5979\u65F6\u5E38\u4F1A\u9677\u5165\u4E00\u79CD\u7126\u8651\uFF0C\u81EA\u5DF1\u4F1A\u4E0D\u4F1A\u4E5F\u53D8\u6210\u4E00\u4E2A\u75AF\u5B50\uFF0C\u4E5F\u4F1A\u50CF\u5988\u5988\u5BF9\u5F85\u5979\u4E00\u6837\uFF0C\u5BF9\u5F85\u81EA\u5DF1\u7684\u5B69\u5B50\u4EEC\u3002</p><p>\u300A\u6BCD\u4EB2\u8BA1\u5212\u300B\u662F\u5979\u57282003\u52302006\u5E74\u671F\u95F4\u7684\u9879\u76EE\u3002\u5979\u56DE\u5230\u4E86\u758F\u8FDC\u5DF2\u4E45\u7684\u6BCD\u4EB2\u8EAB\u8FB9\uFF0C\u8BD5\u7740\u7528\u76F8\u673A\u91CD\u65B0\u8BA4\u8BC6\u6BCD\u4EB2\uFF0C\u4E5F\u91CD\u65B0\u627E\u5230\u81EA\u5DF1\u3002\u8FD9\u4E9B\u7167\u7247\u91CD\u73B0\u4E86Gearon\u5BF9\u81EA\u5DF1\u7AE5\u5E74\u7684\u5370\u8C61\u3002\u5B83\u4EEC\u6709\u7740\u8FF7\u4EBA\u7684\u3001\u4E0D\u786E\u5B9A\u7684\u6C1B\u56F4\uFF0C\u6709\u6E29\u6696\uFF0C\u4E5F\u6709\u65E0\u52A9\uFF0C\u4EA4\u7EC7\u7740\u4EB2\u5BC6\u548C\u4E0D\u5B89\u3002</p><p>\u76F4\u9762\u8FC7\u5F80\u662F\u75DB\u82E6\u7684\uFF0C\u4F46\u5BF9\u5979\u6765\u8BF4\uFF0C\u6444\u5F71\u6C38\u8FDC\u662F\u4E00\u79CD\u6CBB\u7597\u65B9\u5F0F\u3002\u901A\u8FC7\u6444\u5F71Gearon\u4E0D\u4EC5\u53EF\u4EE5\u786E\u8BA4\u90A3\u4E2A\u9700\u8981\u88AB\u4FDD\u62A4\u7684\u81EA\u5DF1\uFF0C\u4E5F\u540C\u6837\u786E\u8BA4\u4E86\u90A3\u4E2A\u9700\u8981\u88AB\u7231\u3001\u88AB\u5C0A\u91CD\u7684\u6BCD\u4EB2\uFF0C\u7EE7\u800C\u4E5F\u8BA9\u8FD9\u4E2A\u5BB6\u5EAD\u4E09\u4EE3\u4EBA\u4E4B\u95F4\u7684\u60C5\u611F\u7EBD\u5E26\u53D8\u5F97\u66F4\u52A0\u7D27\u5BC6\u3002</p><h2 id="\u7B2C\u516B\u5929-\u9AD8\u5C71" tabindex="-1">\u7B2C\u516B\u5929 \uFF5C \u9AD8\u5C71 <a class="header-anchor" href="#\u7B2C\u516B\u5929-\u9AD8\u5C71" aria-hidden="true">#</a></h2><p>\u5728\u4E2D\u56FD\u5BB6\u5EAD\u666E\u904D\u5B58\u5728\u4E00\u79CD\u60C5\u611F\u8868\u8FBE\u969C\u788D\uFF0C\u5728\u6211\u4EEC\u542B\u84C4\u5185\u655B\u7684\u6587\u5316\u4F20\u7EDF\u91CC\uFF0C\u7528\u53E3\u5934\u6216\u80A2\u4F53\u7684\u884C\u4E3A\u6765\u8868\u8FBE\u7231\u5F80\u5F80\u662F\u5FC3\u7406\u4E0A\u5F88\u96BE\u903E\u8D8A\u7684\u4E00\u4E2A\u574E\u3002\u6240\u4EE5\uFF0C\u5728\u4E2D\u56FD\u827A\u672F\u5BB6\u9AD8\u5C71\u7684\u4F5C\u54C1\u300A\u7B2C\u516B\u5929\u300B\u91CC\uFF0C\u5F53\u6211\u770B\u5230\u4E00\u8FDE\u4E32\u5BF9\u65E5\u5E38\u7269\u4F53\u7684\u68C0\u89C6\uFF0C\u8FD8\u6709\u5BF9\u6BCD\u4EB2\u4F53\u6001\u7279\u5F81\u548C\u65E5\u5E38\u884C\u4E3A\u7684\u201C\u65C1\u89C2\u201D\u540E\uFF0C\u90A3\u79CD\u201C\u65E2\u719F\u6089\u53C8\u964C\u751F\u201D\u7684\u611F\u89C9\u4E0D\u7981\u53C8\u6D6E\u73B0\u51FA\u6765\u3002</p><p>\u8C01\u4E0D\u66FE\u82E5\u6709\u6240\u601D\u5730\u51DD\u89C6\u8FC7\u8EAB\u8FB9\u6700\u4EB2\u5BC6\u7684\u4EBA\uFF0C\u867D\u6709\u5343\u8A00\u4E07\u8BED\u6D8C\u4E0A\u5FC3\u5934\uFF0C\u6700\u7EC8\u8FD8\u662F\u5BC4\u671B\u5FC3\u7167\u4E0D\u5BA3\u3002\u6211\u4EEC\u90FD\u575A\u4FE1\u627F\u62C5\u8D23\u4EFB\u662F\u7231\uFF0C\u9ED8\u9ED8\u4ED8\u51FA\u4E5F\u662F\u7231\u3002\u4F46\u8868\u8FBE\u7684\u7F3A\u5931\u5E26\u6765\u7684\u4E0D\u4EC5\u4EC5\u662F\u4EB2\u5BC6\u611F\u7684\u7F3A\u5931\uFF0C\u671F\u95F4\u8FD8\u6F5C\u4F0F\u7740\u758F\u79BB\u548C\u51B7\u6F20\u3002\u65E5\u590D\u4E00\u65E5\u3001\u500F\u5C14\u4E0D\u89C1\uFF0C\u672C\u8BE5\u6700\u4EB2\u5BC6\u65E0\u95F4\u7684\u4EBA\u53CD\u800C\u6210\u4E86\u6700\u719F\u6089\u7684\u964C\u751F\u4EBA\u3002\u4F46\u662F\u5728\u300A\u7B2C\u516B\u5929\u300B\u4E2D\uFF0C\u6211\u4EEC\u770B\u5230\u4E86\u827A\u672F\u5BB6\u5BF9\u4E8E\u8FD9\u79CD\u7F3A\u61BE\u7684\u8B66\u9192\u548C\u5F25\u8865\u3002</p><p>\u8FC2\u56DE\u94FA\u9648\u4E4B\u540E\u7684\u60C5\u611F\u8FF8\u53D1\u662F\u5982\u6B64\u6709\u529B\u5730\u649E\u51FB\u4EBA\u5FC3\u3002\u6BCD\u5B50\u4E8C\u4EBA\u5766\u8BDA\u70ED\u70C8\u7684\u62E5\u62B1\uFF0C\u6709\u7740\u5B97\u6559\u4EEA\u5F0F\u822C\u7684\u5E84\u4E25\u529B\u91CF\uFF0C\u7ED9\u4F3C\u4E4E\u5DF2\u7ECF\u9EBB\u6728\u800C\u6C89\u5BC2\u7684\u751F\u6D3B\u9644\u4E0A\u4E86\u4E00\u5C42\u4E45\u8FDD\u7684\u4EEA\u5F0F\u611F\u3002\u7531\u6B64\uFF0C\u90A3\u5C42\u672C\u4E0D\u5FC5\u6709\u786E\u6709\u65E0\u5904\u4E0D\u5728\u7684\u9694\u9602\u5F97\u4EE5\u88AB\u91CD\u91CD\u5730\u51FB\u788E\u3002\u6BCD\u4EB2\u4E5F\u4ECE\u90A3\u5C42\u56FA\u6709\u7684\u8EAB\u4EFD\u5F62\u8C61\u4E2D\u6E10\u6E10\u5C55\u9732\u51FA\u5979\u4E3A\u201C\u4EBA\u201D\u7684\u6700\u771F\u5B9E\u7684\u9762\u8C8C\u3002</p><h2 id="mother-paul-graham" tabindex="-1">Mother \uFF5C Paul Graham <a class="header-anchor" href="#mother-paul-graham" aria-hidden="true">#</a></h2><p>19\u5F20\u7167\u7247\uFF0C\u540C\u6837\u7684\u573A\u666F\u548C\u89C6\u89D2\u3001\u540C\u6837\u7684\u81EA\u7136\u5149\u7EBF\uFF0C\u753B\u9762\u4ECE\u59CB\u81F3\u7EC8\u90FD\u53EA\u6709\u4E00\u4E2A\u6253\u778C\u7761\u7684\u8001\u592A\u592A\u3002\u8FD9\u662FPaul Graham\u5E74\u8FD190\u5C81\u7684\u6BCD\u4EB2\u3002</p><p>Paul Graham\u5728\u8C08\u5230\u81EA\u5DF1\u7684\u62CD\u6444\u52A8\u673A\u65F6\uFF0C\u4ED6\u8BF4\u5230\uFF1A\u201C\u6211\u60F3\u7528\u6E05\u6F88\u7684\u773C\u775B\u770B\u7740\u5979\u5728\u4E16\u4E0A\u6700\u540E\u7684\u5C81\u6708\u201D\u3002\u4E8E\u662F\u6211\u4EEC\u770B\u5230\uFF0C\u5728\u8FD9\u4E9B\u8096\u50CF\u4E2D\uFF0C\u6CA1\u6709\u82B1\u54E8\u7684\u6280\u6CD5\u3001\u4E5F\u6CA1\u6709\u60CA\u8273\u7684\u77AC\u95F4\uFF0C\u53EA\u6709\u6E29\u6696\u7684\u9633\u5149\u900F\u8FC7\u7A97\u6237\uFF0C\u8FD8\u6709\u547C\u5438\u95F4\u7F13\u7F13\u6D41\u8FC7\u7684\u65F6\u95F4\u3002\u4F60\u751A\u81F3\u53EF\u4EE5\u60F3\u8C61\u8FDE\u76F8\u673A\u7684\u5FEB\u95E8\u58F0\u60F3\u5FC5\u90FD\u662F\u6781\u4E3A\u8F7B\u67D4\u7684\u3002</p><p>\u5982\u679C\u6211\u4EEC\u4ED4\u7EC6\u89C2\u770B\u8FD9\u4E9B\u201C\u7B80\u5355\u201D\u7684\u7167\u7247\uFF0C\u5728\u8096\u50CF\u548C\u8096\u50CF\u4E4B\u95F4\uFF0C\u76F8\u673A\u7684\u7126\u70B9\u6709\u7740\u51E0\u4E4E\u96BE\u4EE5\u89C9\u5BDF\u7684\u8F6C\u79FB\u3002\u6709\u65F6\u65F6\u6BCD\u4EB2\u8138\u4E0A\u7684\u76B1\u7EB9\u3001\u6709\u65F6\u662F\u5979\u94F6\u7070\u8272\u7684\u5934\u53D1\uFF0C\u8FD8\u6709\u5979\u7C89\u8272\u3001\u84DD\u8272\u7684\u6BDB\u886B\u548C\u5C0F\u788E\u82B1\u886C\u8863\u3002Paul Graham\u7528\u53E4\u5178\u753B\u5BB6\u4E00\u822C\u7684\u8010\u5FC3\u548C\u5BF9\u7EC6\u8282\u7684\u7CBE\u786E\u638C\u63A7\u5C06\u6C99\u6F0F\u4E2D\u7684\u65F6\u5149\u6084\u6084\u5730\u5438\u5165\u4ED6\u7684\u955C\u5934\u3002\u8FD9\u4E9B\u8096\u50CF\u548C\u4ED6\u5728\u8857\u5934\u6355\u6349\u5230\u7684\u90A3\u4E9B\u5145\u6EE1\u8BD7\u610F\u7684\u65F6\u523B\u4E00\u6837\uFF0C\u5F15\u9886\u6211\u4EEC\u53BB\u51DD\u89C6\u5E73\u51E1\u751F\u6D3B\u4E2D\u6D41\u901D\u7684\u7F8E\uFF0C\u6211\u4EEC\u53EF\u4EE5\u4E00\u904D\u4E00\u904D\u5730\u9605\u8BFB\u8FD9\u4E9B\u8096\u50CF\uFF0C\u6700\u7EC8\u6211\u4EEC\u90FD\u4F1A\u53D1\u73B0\uFF0C\u8FD9\u4E9B\u770B\u4F3C\u91CD\u590D\u800C\u4E14\u201C\u4E4F\u5473\u201D\u7684\u7167\u7247\u5C31\u5982\u540C\u6BCF\u4E2A\u4EBA\u6240\u7ECF\u5386\u7684\u5C81\u6708\u3002\u5B83\u4EEC\u5E73\u6DE1\u3001\u6C89\u9ED8\uFF0C\u5E26\u7740\u4EB2\u4EBA\u6BEB\u65E0\u4FDD\u7559\u7684\u7231\u3001\u5E26\u7740\u4ED6\u4EEC\u6E29\u67D4\u7684\u575A\u5B88\uFF0C\u7F13\u6162\u800C\u65E0\u60C5\u5730\u6D41\u901D\u3002</p><h2 id="\u6211\u4EEC\u53EF\u4EE5\u62CD\u4EC0\u4E48" tabindex="-1">\u6211\u4EEC\u53EF\u4EE5\u62CD\u4EC0\u4E48\uFF1F <a class="header-anchor" href="#\u6211\u4EEC\u53EF\u4EE5\u62CD\u4EC0\u4E48" aria-hidden="true">#</a></h2><ol><li><p>\u5F53\u7136\u53EF\u4EE5\u62CD\u81EA\u5DF1\u7684\u7ECF\u5386\uFF08\u4F8B\u5982\u5BB6\u5EAD\u9898\u6750\u7B49\uFF09\uFF0C\u4F46\u8FD9\u4E9B\u7167\u7247\u5E94\u8BE5\u8BA9\u89C2\u8005\u53EF\u4EE5\u4EA7\u751F\u5171\u9E23\u3002\u56E0\u4E3A\u89C2\u4F17\u5BF9\u964C\u751F\u7684\u4F60\u4EE5\u53CA\u4F60\u7684\u5BB6\u4EBA\u6CA1\u6709\u4E1D\u6BEB\u5174\u8DA3\u3002\u53EF\u4EBA\u7C7B\u7684\u60C5\u611F\u662F\u5171\u901A\u7684\uFF0C\u4F60\u7684\u7167\u7247\u662F\u5426\u80FD\u591F\u5524\u8D77\u89C2\u4F17\u7684\u60C5\u611F\u8BB0\u5FC6\u3001\u5171\u9E23\u662F\u68C0\u9A8C\u4F60\u662F\u5426\u662F\u4E00\u540D\u5408\u683C\u6444\u5F71\u5E08\u7684\u6807\u51C6\u3002</p></li><li><p>\u5728\u62CD\u6444\u9898\u6750\u65F6\uFF0C\u8FC7\u4E8E\u76F4\u767D\u3001\u6EE5\u60C5\u7684\u8868\u8FBE\u662F\u4E00\u79CD\u5F88\u4F4E\u7EA7\u7684\u5904\u7406\u624B\u6BB5\u3002\u4F60\u4E0D\u7ECF\u601D\u8003\u5730\u6309\u4E0B\u5FEB\u95E8\uFF0C\u8D34\u4E0A\u7EAA\u5B9E\u7684\u6807\u7B7E\uFF0C\u8FD9\u4E9B\u56FE\u7247\u662F\u5982\u6B64\u7684\u6CDB\u6EE5\uFF0C\u5728\u5404\u79CD\u65B0\u95FB\u3001\u793E\u4EA4\u5A92\u4F53\u4E2D\u968F\u5904\u53EF\u89C1\u3002\u7528\u8FD9\u6837\u76F4\u767D\u7684\u3001\u672A\u7ECF\u601D\u8003\u7684\u8868\u8FBE\u624B\u6CD5\u62CD\u6444\u7684\u5185\u5BB9\u53EA\u4F1A\u5728\u4E16\u754C\u591A\u5904\u4E00\u4EFD\u7535\u5B50\u5783\u573E\uFF0C\u6BEB\u65E0\u610F\u4E49\u3002</p></li><li><p>\u5BF9\u4E8E\u6444\u5F71\u6765\u8BF4\uFF0C\u6709\u4E00\u4E2A\u7C97\u6D45\u4F46\u5B9E\u9645\u4E0A\u5F88\u96BE\u505A\u5230\u7684\u4E8B\u60C5\uFF0C\u5C31\u662F\u5173\u5FC3\u4F60\u7684\u62CD\u6444\u5BF9\u8C61\u3002\u662F\u771F\u6B63\u7684\u5173\u5FC3\uFF0C\u4E0D\u53EA\u662F\u77E5\u9053\u4ED6\u662F\u4EC0\u4E48\u6216\u662F\u4EC0\u4E48\u6837\u5B50\u800C\u5DF2\uFF0C\u6700\u597D\u8FD8\u8981\u53BB\u4E86\u89E3\u4E3A\u4EC0\u4E48\uFF1F\u53D1\u751F\u4E86\u4EC0\u4E48\uFF1F\u53EF\u80FD\u8FD8\u4F1A\u53D1\u751F\u4EC0\u4E48\uFF1F</p></li></ol><h2 id="\u53C2\u8003" tabindex="-1">\u53C2\u8003 <a class="header-anchor" href="#\u53C2\u8003" aria-hidden="true">#</a></h2><ul><li><a href="https://www.bilibili.com/video/BV1yy4y1r75v" target="_blank" rel="noreferrer">\u4ED6\u4EEC\u4E3A\u4F55\u800C\u521B\u4F5C\u3010\u6444\u5F71\u52A1\u865A\u7B14\u8BB0\u3011</a></li><li><a href="https://www.bilibili.com/video/BV1tP4y157bo" target="_blank" rel="noreferrer">\u6444\u5F71\u5982\u4F55\u5904\u7406\u7CBE\u795E\u75BE\u75C5\u9898\u6750\u3010\u6444\u5F71\u52A1\u865A\u7B14\u8BB0\u3011</a></li></ul>',20),h=[l];function n(d,o,p,s,c,_){return r(),a("div",null,h)}const y=e(t,[["render",n]]);export{m as __pageData,y as default};
